mapper.select:
SELECT ID, 
FROM News
WHERE NewsType = #{newsType}
    
       and newsSubType = #newsSubType
    
    
        and locationCode = #{locationCode}
    
    
        and ID > #{ID}
      order by ID Limit 0, #{count}

SELECT NN.ID ID, subject, newsType, newsSubType, postDate, locationCode,
isHot, NN.CreateAt CreateAt, NN.CreateBy CreateBy, publisherId, NickName publishSource
FROM News NN
inner join User UR on NN.publisherId = UR.ID
WHERE NewsType = #{newsType}

    and newsSubType = #{newsSubType}


    and locationCode = #{locationCode}


    and ID < #{ID}
  order by ID desc Limit 0, #{count}

SELECT NN.ID ID, subject, newsType, newsSubType, postDate, locationCode,
isHot, NN.CreateAt CreateAt, NN.CreateBy CreateBy, publisherId, NickName publishSource
FROM News NN
inner join User UR on NN.publisherId = UR.ID
WHERE NewsType = #{newsType}

    and newsSubType = #{newsSubType}


    and locationCode = #{locationCode}

order by ID desc Limit #{offset}, #{pageSize}
