mapper.select:
SELECT ID, 
FROM News
WHERE NewsType = #{newsType}
    
       and newsSubType = #newsSubType
    
    
        and locationCode = #{locationCode}
    
    
        and ID > #{ID}
      order by ID Limit 0, #{count}

SELECT ID, 
FROM News
WHERE NewsType = #{newsType}

    and newsSubType = #newsSubType


    and locationCode = #{locationCode}


    and ID < #{ID}
  order by ID desc Limit 0, #{count}
