mapper.select:
SELECT id, 
FROM News
WHERE NewsType = #{newsType}
    
       and newsSubEnum = #newsSubEnum
    
    
        and location = #{location}
    
    
        and id > #{id}
      order by id Limit 0, #{count}

SELECT id, 
FROM News
WHERE NewsType = #{newsType}

    and newsSubEnum = #newsSubEnum


    and location = #{location}


    and id < #{id}
  order by id desc Limit 0, #{count}
